import React from "react";

import DropDown from "./Dropdown.js";

class Filter extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let fieldButtons = this.props.fieldValues.map((item, i) => {
      return (
        <button
          className="uk-button uk-button-default uk-button-small"
          onClick={this.props.handleChange}
          value={item}
          name="fieldSortPreference"
        >
          {" "}
          {item}
        </button>
      );
    });

    return (
      <div>
        <p>Fix me im ugly. Replace with UIKIT dropdown.</p>

        <div className="uk-button-group">{fieldButtons}</div>

        <ul>
          <li>
            <DropDown
              options={["All"].concat(this.props.fieldValues)}
              name="fieldSortPreference"
              handleChange={this.props.handleChange}
              selected={this.props.fieldSortPreference}
            />
          </li>
          <br />
          <li>
            <DropDown
              options={["All"].concat(this.props.yearValues)}
              name="yearSortPreference"
              handleChange={this.props.handleChange}
              selected={this.props.yearSortPreference}
            />
          </li>
          <br />
        </ul>
      </div>
    );
  }
} //END OF CLASS

export default Filter;
