import React from "react";
import { Link } from "react-router-dom";

class ProjectModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      likes: 0
    };
    this.handleLike = this.handleLike.bind(this);
  }

  handleLike() {
    this.setState(prevState => ({
      likes: prevState.likes + 1
    }));
  }

  render() {
    const fields = this.props.fields.map(item => (
      <button
        key={item.toString()}
        className="tag is-dark"
        value={item.toString()}
        name="fieldSortPreference"
        onClick={this.props.handleChange}
      >
        {item.toString()}
      </button>
    ));
    let created = new Date(this.props.created * 1000)
      .toLocaleString()
      .substring(0, 10);

    return (
      <div className="box has-background-black">
        <div className=" card-header ">
          <Link to={`/project_page/${this.props.id}`}>
            <p className="title is-4 has-text-light has-text-weight-normal">
              {this.props.name}
            </p>
          </Link>
        </div>
        <br />
        <div className=" card-image">
          <Link to={`/project_page/${this.props.id}`}>
            <figure className="image">
              <img src={this.props.cover} alt="cover pic" />
            </figure>
          </Link>
        </div>

        <div className=" card-content has-background-black">
          <div className="media">
            <div className="media-content ">
              <div className="content ">
                <div className="tags">{fields}</div>
                <p className="title is-7 has-text-light is-italic">{created}</p>
                <br />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ProjectModule;
