import React, { Component } from "react";
// import ReactDOM from "react-dom";
// import { Player } from "video-react";
// import "../css/video-react.css";
import axios from "axios";

class ProjectPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      project: {},
      projectLoaded: false
    };
  }

  componentDidMount() {
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/projects/${
          this.props.match.params.id
        }?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava`
      )
      .then(response => {
        console.log(response.data);
        this.setState({
          project: response.data.project,
          projectLoaded: true
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    //convert the date from a unix time code to a string and only display
    //the relevant characters
    let created = new Date(this.state.project.created_on * 1000)
      .toLocaleString()
      .substring(0, 10);

    return this.state.projectLoaded ? (
      <div>
        <ProjectInfo project={this.state.project} />
      </div>
    ) : (
      <div className="uk-grid-small " uk-grid="true">
        <span className="uk-align-center" uk-spinner="ratio: 7.5" />
      </div>
    );
  }
}

const ProjectInfo = props => {
  console.log(props);

  const created = new Date(props.project.created_on * 1000)
    .toLocaleString()
    .substring(0, 10);

  let fields = props.project.fields;
  for (let i = 0; i < fields.length; i++) {
    fields[i] = fields[i].toUpperCase();
  }
  fields = fields.join(` | `);

  let tags = props.project.tags;
  for (let i = 0; i < tags.length; i++) {
    tags[i] = tags[i].toUpperCase();
  }
  tags = tags.join(` | `);

  let tools = props.project.tools;
  for (let i = 0; i < tools.length; i++) {
    tools[i] = tools[i].toUpperCase();
  }
  tools = tools.join(` | `);

  return (
    <div>
      <article className="uk-article">
        <p className="uk-article-title">
          <a className="uk-link-reset" href="">
            {props.project.name}
          </a>
        </p>

        <p className="">Project Info</p>

        <p className="uk-text-lead">{props.project.description}</p>

        <p className="uk-article-meta">
          <b>Creative Fields:</b> {fields}
        </p>

        <p className="uk-article-meta">
          <b>Project Tags:</b> {tags}
        </p>

        <p className="uk-article-meta">
          <b>Tools Used:</b> {tags}
        </p>

        <p className="uk-article-meta">
          <b>Date Posted </b>: {created}
        </p>
      </article>
    </div>
  );
};

export default ProjectPage;
