import React from "react";
import { BrowserRouter, Route, Link } from "react-router-dom";
import Portfolio from "./Portfolio";
import About from "./About";
import Contact from "./Contact";

import "./css/style.css";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      profile: []
    };
  }

  render() {
    //the BrowserRouter will render the appropriate component based on the URL path allowing for a single page app
    return (
      <BrowserRouter>
        <div>
          <Navbar />
          // <Route path="/about" component={About} />
          // <Route path="/contact" component={Contact} />
        </div>
      </BrowserRouter>
    );
  }
} //END OF CLASS

// Navbar component for navigation of the site
function Navbar() {
  return (
    <div>
      <nav className="navbar-brand has-background-black-bis">
        <div className="container level">
          <div className="navbar-menu">
            <div className="navbar-start">
              <div className="navbar-item level-item ">
                <Link to="/javawax">
                  <p className="title is-4 level-item has-text-centered has-text-light  has-text-weight-bold">
                    Javawax
                  </p>
                </Link>
              </div>
            </div>

            <div className="navbar-end">
              <div className="navbar-item">
                <Link to="/javawax">
                  <p className="subtitle is-7 level-item has-text-centered has-text-light  has-text-weight-bold">
                    Portfolio
                  </p>
                </Link>
              </div>

              <div className="navbar-item">
                <Link to="/About">
                  <p className="subtitle is-7 level-item has-text-centered has-text-light  has-text-weight-bold">
                    About
                  </p>
                </Link>
              </div>

              <div className="navbar-item">
                <Link to="/contact">
                  <p className="subtitle is-7 level-item has-text-centered has-text-light  has-text-weight-bold">
                    Contact
                  </p>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default App;
