import React from "react";
import { HashRouter, Route, Link } from "react-router-dom";
import "./css/style.css";
import axios from "axios";

import LandingPage from "./LandingPage.js";
import GalleryPage from "./GalleryPage.js";
import AboutPage from "./About.js";
import ContactPage from "./Contact.js";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    //make an axios request for the project by appending the ID passed from the projectModule component to the URL
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/projects/${
          this.props.match.params.id
        }?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava`
      )
      .then(response => {
        this.setState({
          projectName: response.data.project.name,
          projectFields: response.data.project.fields,
          created: response.data.project.created_on,
          projectModules: response.data.project.modules
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    return (
      <HashRouter>
        <div>
          <Link to="/landing">
            <p>Home</p>
          </Link>

          <Link to="/gallery">
            <p>Project Gallery</p>
          </Link>

          <Link to="/about">
            <p>About</p>
          </Link>

          <Link to="/contact">
            <p>Contact</p>
          </Link>

          <div>
            <Route path="/landing" component={LandingPage} />
            <Route path="/gallery" component={GalleryPage} />
            <Route path="/about" component={AboutPage} />
            <Route path="/contact" component={ContactPage} />
          </div>
        </div>
      </HashRouter>
    );
  }
} //END OF CLASS

export default App;
