import React from "react";

import DropDown from "./Dropdown.js";

class Filter extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="box has-background-black is-one-quarter is-offset-0">
        <p className="title is-4 has-text-light has-text-weight-bold">
          Filter Projects
        </p>
        <ul>
          <li>
            <br />
            <DropDown
              options={["All"].concat(this.props.fieldValues)}
              name="fieldSortPreference"
              handleChange={this.props.handleChange}
              selected={this.props.fieldSortPreference}
            />
          </li>
          <br />
          <DropDown
            options={["Newest to Oldest", "Oldest to Newest"]}
            name="dateSortPreference"
            handleChange={this.props.handleChange}
            selected={this.props.dateSortPreference}
          />
          <li />
        </ul>
      </div>
    );
  }
} //END OF CLASS

export default Filter;
