import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Player } from "video-react";
import "./css/video-react.css";
import "./css/style.css";
import axios from "axios";

class ProjectPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      projectName: ``,
      projectFields: [],
      created: ``,
      projectModules: []
    };
  }

  componentDidMount() {
    //make an axios request for the project by appending the ID passed from the projectModule component to the URL
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/projects/${
          this.props.match.params.id
        }?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava`
      )
      .then(response => {
        this.setState({
          projectName: response.data.project.name,
          projectFields: response.data.project.fields,
          created: response.data.project.created_on,
          projectModules: response.data.project.modules
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    //map the project fields into display tags
    const fields = this.state.projectFields.map(item => (
      <span key={item.toString()} className="tag is-dark">
        {item.toString()}
      </span>
    ));

    //convert the date from a unix time code to a string and only display
    //the relevant characters
    let created = new Date(this.state.created * 1000)
      .toLocaleString()
      .substring(0, 10);

    // map over the modules recieved from the response and build the chunks to render
    const projectModules = this.state.projectModules.map(item => {
      //if there is any text in the module...
      if (item.type === "text") {
        return (
          <div key={item.id}>
            <div className="content">
              <br />
              <p className="subtitle is-7 has-text-black-bis   has-text-weight-normal">
                {item.text_plain}
              </p>
              <br />
              <div className="tags">{fields}</div>
              <br />
              <p className="subtitle is-7 has-text-black-bis  is-italic has-text-weight-light">
                {created}
              </p>
            </div>
          </div>
        );
        //if there is any images in the module...
      }
      if (item.type === "image") {
        return (
          <div key={item.id}>
            <div className="card-image has-background-grey-darker">
              <figure className="box image is-3by2">
                <img src={item.src} alt="an art piece" />
              </figure>
            </div>
          </div>
        );
        //if there is any video in the modules...
      }
      if (item.type === "video") {
        console.log(item.src);
        return (
          <div key={item.id}>
            <div className="has-background-grey-darker">
              <div className="iframe-container">
                <iframe
                  className="padRight"
                  src={item.src}
                  width="100%"
                  height="auto"
                  frameBorder="5"
                  webkitallowfullscreen="true"
                  mozallowfullscreen="true"
                  allowFullScreen
                />
              </div>
            </div>
          </div>
        );
      } else {
        //if there is nothing, return null
        return null;
      }
    });

    return (
      <div className="columns body-columns">
        <div className="column is-8 is-offset-2">
          <br />
          <p className="title is-4 has-text-black-bis   has-text-weight-bold">
            {this.state.projectName}
          </p>

          {projectModules}

          <div className="card-content">
            <div className="level is-mobile">
              <div className="level-left" />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ProjectPage;
