import React from "react";

class VideoModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = { module: props.module, iframeLoaded: false };
    this.iframeLoadedCallBack = this.iframeLoadedCallBack.bind(this);
  }

  iframeLoadedCallBack() {
    console.log("loaded");
  }

  render() {
    const iframe = document.querySelector("#module_frame");
    if (iframe) {
      iframe.onload = () => {
        this.setState({ iframeLoaded: true });
        console.log("iframe is completely loaded");
      };
    }

    console.log(this.state);
    return this.state.iframeLoaded ? (
      <div className="uk-width-1-1@s uk-width-2-3@m uk-row-first uk-margin-large-bottom uk-align-center ">
        <iframe
          id="module_frame"
          src={this.state.module.src}
          width="1920"
          height="1080"
          frameBorder="0"
          allowFullScreen={true}
          uk-responsive="true"
          uk-video="automute: true; autoplay: true"
          className="uk-responsive-width"
        />
      </div>
    ) : (
      <div className="uk-width-1-1@s uk-width-2-3@m uk-row-first uk-margin-large-bottom uk-align-center ">
        <span className="uk-align-center" uk-spinner="ratio: 7.5" />

        <hr />
      </div>
    );
  }
}

export default VideoModule;
