import React from "react";
import { Link } from "react-router-dom";

class ProjectModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = { project: this.props.data };
  }

  render() {
    const project = this.state.project;

    //REDUCE SIZE TO OPTIMIZE LOADING TIMES
    const thumbnail = project.covers[808];

    const fields = project.fields.map(item => (
      <button
        key={item.toString()}
        value={item.toString()}
        name="fieldSortPreference"
      >
        {item.toString()}
      </button>
    ));
    let created = new Date(project.created_on * 1000)
      .toLocaleString()
      .substring(0, 10);

    return (
      <div class="uk-text-center uk-width-1-3@s">
        <div class="uk-inline-clip uk-transition-toggle uk-light" tabindex="0">
          <img src="images/dark.jpg" alt="" />
          <div class="uk-position-center">
            <span class="uk-transition-fade" uk-icon="icon: plus; ratio: 2" />
          </div>
        </div>
        <p class="uk-margin-small-top">Icon</p>
      </div>
    );
  }
}

export default ProjectModule;

