import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import ProjectModule from './ProjectModule';
import DropDown from './components/DropDown.js';

class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userProjects: [],
      searchTerm: '',
      dateSortPreference: 'Newest to Oldest',
      fieldValues: [],
      fieldSortPreference: 'All'
    };
    this.handleSearchChange = this.handleSearchChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleSearchChange(event) {
    const value = event.target.value;
    this.setState({ searchTerm: value });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    console.log(target);
    console.log(value);
    console.log(name);
    this.setState({ [name]: value });
  }

  componentDidMount() {
    ReactDOM.findDOMNode(this).scrollTop = 0;

    axios
      .get(
        'https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/users/288284785/projects?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava'
      )
      .then(response => {
        this.setState({ userProjects: response.data.projects });
        let fields = this.state.userProjects
          .map(item => {
            return item.fields;
          })
          .toString()
          .split(',');
        const deduped = [...new Set(fields)];
        deduped.sort();
        this.setState({ fieldValues: deduped });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    //Sort the data based on the selected dateSortPreference
    const data = [].concat(this.state.userProjects).sort((a, b) => {
      if (a.created_on < b.created_on)
        return this.state.dateSortPreference === 'Oldest to Newest' ? -1 : 1;
      if (a.created_on > b.created_on)
        return this.state.dateSortPreference === 'Oldest to Newest' ? 1 : -1;
      return 0;
    });

    //check to see if the project matches the search term
    let projectList = data.map(item => {
      const nameMatch = item.name
        .toLowerCase()
        .startsWith(this.state.searchTerm.toLowerCase());

      // check to see if the project matches the field search term
      const fieldMatch =
        item.fields.includes(this.state.fieldSortPreference) ||
        this.state.fieldSortPreference === 'All';

      // if the project matches the parameters, return it as a Project Module component
      // if not, return nothing
      return nameMatch && fieldMatch ? (
        <div className="column is-one-quarter projectModule" key={item.id}>
          <ProjectModule
            key={item.id}
            id={item.id}
            name={item.name}
            fields={item.fields}
            cover={item.covers[230]}
            url={item.url}
            created={item.created_on}
            linkUrl={item.url}
            handleChange ={this.handleChange}
          />
        </div>
      ) : null;
    });

    return (
      <div>
        <Title name="Javawax" />
        <section className="section">
          <div className="container has-background-light">
            <div className=" column is-one-quarter" />
            <hr />
            <div className="columns is-multiline">
              <div className=" column is-one-quarter">
                <div className="box has-background-black is-one-quarter is-offset-0">
                  <p className="title is-4 has-text-light has-text-weight-bold">
                    Filter Projects
                  </p>
                  <ul>
                    <li>
                      <input
                        className="input"
                        type={'search'}
                        name={'searchText'}
                        value={this.state.searchTerm}
                        onChange={this.handleSearchChange}
                        placeholder={'By Name...'}
                      />
                    </li>
                    <li>
                      <br />
                      <DropDown
                        options={['All'].concat(this.state.fieldValues)}
                        name="fieldSortPreference"
                        handleChange={this.handleChange}
                        selected={this.state.fieldSortPreference}
                      />
                    </li>
                    <br />
                    <DropDown
                      options={['Newest to Oldest', 'Oldest to Newest']}
                      name="dateSortPreference"
                      handleChange={this.handleChange}
                      selected={this.state.dateSortPreference}
                    />
                    <li />
                  </ul>
                </div>
              </div>

              <div className="column is-three-quarters">
                <p className="title is-1 has-text-right has-text-black-bis has-text-weight-bold">
                  This is my Portfolio.
                </p>
                <p className="subtitle is-4 has-text-right has-text-black-bis">
                  You're here now so you may as well have a look.
                </p>
              </div>
              {projectList}
            </div>
          </div>
        </section>
      </div>
    );
  }
}

// title prop
function Title(props) {
  return (
    <section>
      <div className="hero is-fullheight  has-background-light">
        <div className="hero-body">
          <div className="container">
            <p className="title is-1 has-text-left has-text-black-bis has-text-weight-bold">
              {props.name}
            </p>
            <p className="subtitle is-4 has-text-left has-text-black-bis">
              Scroll down to see some stuff I made.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Portfolio;
