import React, { Component } from "react";
import ReactDOM from "react-dom";
import axios from "axios";

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      social_links: []
    };
  }

  // when component mounts make an axois request for the social links and then used a CDN to retrive the
  //social media icons based on thr data returned from the axois request
  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/http://behance.net/v2/users/288284785?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava"
      )
      .then(response => {
        this.setState({
          social_links: response.data.user.social_links.map(item => (
            <i key={item.social_id} className="level-item">
              <a href={item.url} taget="_blank" rel="noopener noreferrer">
                <figure>
                  <img
                    src={`https://cdnjs.cloudflare.com/ajax/libs/webicons/2.0.0/webicons/webicon-${item.service_name.toLowerCase()}.png`}
                    alt="icon"
                  />
                </figure>
              </a>
            </i>
          ))
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        <section className="hero is-fullheight-with-navbar ">
          <div className="hero-body has-background-lighter">
            <div className="container">
              <p className="title is-1 has-text-left has-text-black-bis has-text-weight-bold">
                Want to get in contact?
              </p>
              <br />
              <p className="subtitle is-5 has-text-left has-text-black-bis  has-text-weight-normal">
                For any questions or enquiries e-mail{" "}
                <b>danielrdhr@gmail.com</b>
              </p>
              <p className="subtitle is-6 has-text-left has-text-black-bis  has-text-weight-normal">
                Follow me on social media.
              </p>
              <div className=" level-left icon-small subtitle is-6 has-text-left has-text-black-bis has-text-weight-normal">
                {this.state.social_links}
              </div>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default Contact;
