import React from "react";

class VideoModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = { module: props.module };
  }
  render() {
    console.log(this.state.module);
    return (
      <div>
        <h3>Autoplay Inview</h3>

        <button
          className="uk-button uk-button-default uk-margin-bottom"
          type="button"
          uk-toggle="target: +"
        >
          Toggle Video
        </button>
        <iframe
          src={this.state.module.src}
          width="1920"
          height="1080"
          frameborder="0"
          allowfullscreen=""
          uk-responsive=""
          uk-video="automute: true; autoplay: inview"
          className="uk-responsive-width"
        />
      </div>
    );
  }
}

export default VideoModule;

