import React from "react";
import { Link } from "react-router-dom";

class ProjectModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = { project: this.props.data };
  }

  render() {
    const project = this.state.project;

    //REDUCE SIZE TO OPTIMIZE LOADING TIMES
    const thumbnail = project.covers[808];

    const fields = project.fields.map(item => (
      <li
        key={item.toString()}
        value={item.toString()}
        name="fieldSortPreference"
      />
    ));

    let created = new Date(project.created_on * 1000)
      .toLocaleString()
      .substring(0, 10);

    let fieldTags = ``;
    project.fields.forEach(item => {
      fieldTags += `${item} | `;
    });

    return (
      <div className="uk-text-center uk-width-1-3@s">
        <Link to={`/project_page/${project.id}`}>
          <div className="uk-inline-clip uk-transition-toggle" tabIndex="0">
            <img src={thumbnail} alt="" />
            <div className="uk-transition-fade uk-position-cover uk-position-small uk-overlay uk-overlay-default">
              <p className="uk-h3 ">
                {project.name}
                <br />
              </p>
              <p className="uk-h4 ">
                {created}
                <br />
              </p>
              <p className="uk-h5 ">{fieldTags}</p>
            </div>
          </div>
        </Link>
      </div>
    );
  }
}

export default ProjectModule;

