import React from "react";
import "./css/style.css";

import axios from "axios";

import ProjectList from "./components/ProjectList.js";
import DropDown from "./components/Dropdown.js";

class GalleryPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      projects: [],
      projectsLoaded: false,
      searchTerm: "",
      dateSortPreference: "New to Old",
      fieldValues: [],
      fieldSortPreference: "All"
    };

    this.handleSearchChange = this.handleSearchChange.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleSearchChange(event) {
    const value = event.target.value;
    this.setState({ searchTerm: value });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    // console.log(target);
    // console.log(value);
    // console.log(name);
    this.setState({ [name]: value });
  }

  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/users/288284785/projects?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava"
      )
      .then(response => {
        //Limit the number loaded initally to 10 and add a LOAD MORE button to get the rest
        this.setState({
          projects: response.data.projects,
          projectsLoaded: true
        });

        let fields = response.data.projects
          .map(item => {
            return item.fields;
          })
          .toString()
          .split(",");
        const deduped = [...new Set(fields)];
        deduped.sort();
        this.setState({ fieldValues: deduped });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    // console.log(this.state);

    //Sort projects by date created
    console.log(this.state.projects);
    const projectsSortedByDate = [].concat(this.state.projects).sort((a, b) => {
      if (a.created_on < b.created_on)
        return this.state.dateSortPreference === "New to Old" ? 1 : -1;
      if (a.created_on > b.created_on)
        return this.state.dateSortPreference === "New to Old" ? -1 : 1;
      return 0;
    });
    console.log(projectsSortedByDate);
    const projectsFilteredByField = projectsSortedByDate.filter(item => {
      return (
        item.fields.includes(this.state.fieldSortPreference) ||
        this.state.fieldSortPreference === "All"
      );
    });

    const projectsToBeRendered = projectsFilteredByField;
    console.log(projectsToBeRendered);

    return (
      <div>
        <div className="uk-grid uk-margin-large-bottom">
          <div className="uk-width-1-1 uk-row-first">
            <h3 className="uk-heading-large">Portfolio</h3>
            <p className="uk-text-large">
              Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
              eiusmod tempor incididunt ut.
            </p>
          </div>
        </div>

        <div className="box has-background-black is-one-quarter is-offset-0">
          <p className="title is-4 has-text-light has-text-weight-bold">
            Filter Projects
          </p>
          <ul>
            <li>
              <input
                className="input"
                type={"search"}
                name={"searchTerm"}
                value={this.state.searchTerm}
                onChange={this.handleChange}
                placeholder={"By Name..."}
              />
            </li>
            <li>
              <br />
              <DropDown
                options={["All"].concat(this.state.fieldValues)}
                name="fieldSortPreference"
                handleChange={this.handleChange}
                selected={this.state.fieldSortPreference}
              />
            </li>
            <br />
            <DropDown
              options={["Newest to Oldest", "Oldest to Newest"]}
              name="dateSortPreference"
              handleChange={this.handleChange}
              selected={this.state.dateSortPreference}
            />
            <li />
          </ul>
        </div>

        <div>
          {this.state.projectsLoaded ? (
            <ProjectList projects={projectsFilteredByField} />
          ) : (
            <div
              className="uk-grid-small "
              uk-grid="masonry: true; parallax: 100"
            >
              <span className="uk-align-center" uk-spinner="ratio: 7.5" />
            </div>
          )}
        </div>
      </div>
    );
  }
} //END OF CLASS

export default GalleryPage;
