import React from "react";

import DropDown from "./Dropdown.js";

class Filter extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let fieldButtons = this.props.fieldValues.map((item, i) => {
      return (
        <button
          key={item}
          className="uk-button uk-button-default uk-button-small "
          onClick={this.props.handleChange}
          value={item}
          name="fieldSortPreference"
        >
          {item}
        </button>
      );
    });

    let yearButtons = this.props.yearValues.map((item, i) => {
      return (
        <button
          key={item}
          className="uk-button uk-button-default uk-button-small uk-margin-small-bottom"
          onClick={this.props.handleChange}
          value={item}
          name="yearSortPreference"
        >
          {item}
        </button>
      );
    });

    const filtertext =
      (this.props.fieldSortPreference === "All"
        ? `Displaying all projects from `
        : `Displaying ${this.props.fieldSortPreference} projects from `) +
      (this.props.yearSortPreference === "All"
        ? `all years.`
        : `${this.props.yearSortPreference}`);

    return (
      <div>
        <div className="uk-margin-large-bottom">
          <p>Filter projects by year.</p>
          <div>{yearButtons}</div>
        </div>

        <div className="uk-margin-large-bottom">
          <p>Filter projects by category.</p>
          <div>{fieldButtons}</div>
        </div>
        <p>{filtertext}</p>
        <hr class="uk-divider-icon" />
      </div>
    );
  }
} //END OF CLASS

export default Filter;
