import React from "react";
import { Link } from "react-router-dom";

class ProjectModule extends React.Component {
  constructor(props) {
    super(props);
    this.state = { project: this.props.data };
  }

  render() {
    const project = this.state.project;

    //REDUCE SIZE TO OPTIMIZE LOADING TIMES
    const thumbnail = project.covers[808];

    const fields = project.fields.map(item => (
      <button
        key={item.toString()}
        value={item.toString()}
        name="fieldSortPreference"
      >
        {item.toString()}
      </button>
    ));
    let created = new Date(project.created_on * 1000)
      .toLocaleString()
      .substring(0, 10);

    return (
      <div className="uk-width-1-3@s">
        <p>{project.name}</p>
        <Link to={`/project_page/${project.id}`}>
          <figure className="image">
            <img src={thumbnail} alt="cover pic" />
          </figure>
        </Link>
        <div className="tags">{fields}</div>
        <hr />

        <div className="uk-card-small uk-card-body uk-inline uk-width-1-3@s">
          <Link to={`/project_page/${project.id}`}>
            <div className="uk-inline-clip uk-transition-toggle" tabIndex="0">
              <img
                data-src={this.props.post.post_imageURL}
                width="100%"
                alt=""
                uk-img="true"
              />

              <div className="uk-transition-fade uk-position-cover uk-position-small uk-overlay uk-overlay-default uk-flex uk-flex-center uk-flex-middle">
                <h2 className="uk-text-center uk-text-capitalize">
                  {this.props.post.post_title}
                </h2>
              </div>
            </div>
          </Link>
        </div>
      </div>
    );
  }
}

export default ProjectModule;
