import React, { Component } from "react";
// import ReactDOM from "react-dom";
// import { Player } from "video-react";
// import "../css/video-react.css";
import axios from "axios";

class ProjectPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      project
    };
  }

  componentDidMount() {
    //make an axios request for the project by appending the ID passed from the projectModule component to the URL
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/projects/${
          this.props.match.params.id
        }?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava`
      )
      .then(response => {
        console.log(response.data);
        this.setState({
          project: response.data.project
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    //map the project fields into display tags
    const fields = this.state.projectFields.map(item => (
      <span key={item.toString()} className="tag is-dark">
        {item.toString()}
      </span>
    ));

    //convert the date from a unix time code to a string and only display
    //the relevant characters
    let created = new Date(this.state.created * 1000)
      .toLocaleString()
      .substring(0, 10);

    return (
      <div>
        <div>
          <br />
          <p>{this.state.project.name}</p>

          <div>
            <div>
              <div />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ProjectPage;
