import React from "react";

import DropDown from "./Dropdown.js";

class Filter extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    let fieldButtons = this.props.fieldValues.map((item, i) => {
      return (
        <button
          key={item}
          className="uk-button uk-button-default uk-button-small uk-margin-small-right"
          onClick={this.props.handleChange}
          value={item}
          name="fieldSortPreference"
        >
          {item}
        </button>
      );
    });

    let yearButtons = this.props.yearValues.map((item, i) => {
      return (
        <button
          key={item}
          className="uk-button uk-button-default uk-button-small "
          onClick={this.props.handleChange}
          value={item}
          name="yearSortPreference"
        >
          {item}
        </button>
      );
    });

    return (
      <div>
        <div uk-grid="true">
          <p>Filter projects by category.</p>
          <div uk-grid="true">{fieldButtons}</div>
          <hr />
        </div>

        <div>
          <p>Filter projects by year.</p>
          <div className="uk-button-group uk-align-center">{yearButtons}</div>
        </div>
        <hr />
      </div>
    );
  }
} //END OF CLASS

export default Filter;
