import React, { Component } from "react";
import ReactDOM from "react-dom";
import axios from "axios";

class About extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profilePic: "",
      first_name: "",
      last_Name: "",
      occupation: "",
      fields: [],
      company: "",
      location: "",
      bio: ""
    };
  }

  // make an axios request for the approprite data
  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/https://cors-anywhere.herokuapp.com/http://behance.net/v2/users/288284785?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava"
      )
      .then(response => {
        this.setState({
          profilePic: response.data.user.images[276],
          first_name: response.data.user.first_name,
          last_name: response.data.user.last_name,
          occupation: response.data.user.occupation,
          company: response.data.user.company,
          location: response.data.user.location,
          bio: response.data.user.sections["About Myself"]
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  //render the bio section, but make it the same color as the background if the data has not been loaded
  render() {
    return (
      <div>
        <section className="hero is-fullheight-with-navbar">
          <div className="hero-body has-background-lighter">
            <div className="container">
              <br />
              <p
                className={
                  this.state.first_name
                    ? " title is-1 has-text-left has-text-weight-bold has-text-black-bis "
                    : " has-text-light  "
                }
              >
                Hi, I'm {this.state.first_name}
              </p>
              <br />
              <p
                className={
                  this.state.occupation
                    ? "subtitle is-6 has-text-left is-italic has-text-grey-darker"
                    : "has-text-light "
                }
              >
                {this.state.occupation} at {this.state.company},{" "}
                {this.state.location}.
              </p>
              <p className="subtitle is-5 has-text-left has-text-black-bis  has-text-weight-normal">
                {this.state.bio}
              </p>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default About;
