import React, { Component } from "react";
// import ReactDOM from "react-dom";
// import { Player } from "video-react";
// import "../css/video-react.css";
import axios from "axios";

class ProjectPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      project: {}
    };
  }

  componentDidMount() {
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://www.behance.net/v2/projects/${
          this.props.match.params.id
        }?api_key=EnQn6zgYBKyZh2keUdkI1FdONQn7rava`
      )
      .then(response => {
        console.log(response.data);
        this.setState({
          project: response.data.project
        });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    //convert the date from a unix time code to a string and only display
    //the relevant characters
    let created = new Date(this.state.project.created_on * 1000)
      .toLocaleString()
      .substring(0, 10);

    return (
      <div>
        <ProjectInfo project={this.state.project} />
      </div>
    );
  }
}

const ProjectInfo = props => {
  return (
    <div>
      <article class="uk-article">
        <h1 class="uk-article-title">
          <a class="uk-link-reset" href="">
            {props.project.name}
          </a>
        </h1>

        <i>
          <p class="uk-article-meta">
            CREATED: {new Date(props.project.created_on * 1000)}
          </p>
        </i>

        <p class="uk-text-lead">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip.
        </p>

        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat. Duis aute irure dolor in
          reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
          pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
          culpa qui officia deserunt mollit anim id est laborum.
        </p>

        <div class="uk-grid-small uk-child-width-auto" uk-grid>
          <div>
            <a class="uk-button uk-button-text" href="#">
              Read more
            </a>
          </div>
          <div>
            <a class="uk-button uk-button-text" href="#">
              5 Comments
            </a>
          </div>
        </div>
      </article>
    </div>
  );
};

export default ProjectPage;
